mapper.select:
select m.*, n.userid, n.icon from t_comment m left join t_users n on
m.uid=n.uid where 1=1
	and m.termId= #{termId}

ORDER BY id DESC LIMIT
#{offset},#{limit}

select count(*) from t_comment where 1=1
and termId=#{termId}
order by id desc

select id,termId as atid,uid from t_comment where 1=1
and
termId=#{termId}
and uid=#{uid}

mapper.update:
update t_comment set
commentStatus=#{commentStatus} where 1=1 and
termId=#{termId}
and uid=#{uid}
